mapper.update:
update SY_RKGL_LDRKDJB
set  
     ZZBZ = #{zzbz,jdbcType=VARCHAR},
     ZXSJ= #{zxsj,jdbcType=DATE}
     where LDID = #{ldid,jdbcType=VARCHAR}

update SY_RKGL_LDRKDJB
set  
     ZZBZ = #{zzbz,jdbcType=VARCHAR},
     ZXSJ= #{zxsj,jdbcType=DATE}
     where LDID = #{ldid,jdbcType=VARCHAR}

mapper.select:

SELECT

	

FROM  SY_RKGL_LDRKDJB T1 WHERE 1=1

	


	ORDER BY T1.${sort} ${order}

     select 
        
			
		
  	 from SY_RKGL_LDRKDJB T1  where T1.LDID in (select fw.RYID from SY_FW_LDRK fw where fw.FJBH=#{jzwfjid,jdbcType=VARCHAR}
  	 and fw.deltag!=1)

   
    select 
        
			
		
  	 from SY_RKGL_LDRKDJB T1  where T1.LDID in (select fw.RYID from SY_FW_LDRK fw where fw.FJBH=#{jzwfjid,jdbcType=VARCHAR}
  	 and fw.deltag!=1)

		select count(1)
  	  from SY_RKGL_LDRKDJB T1  where T1.LDID in (select fw.RYID from SY_FW_LDRK fw where fw.FJBH=#{jzwfjid,jdbcType=VARCHAR}
  	 and fw.deltag!=1)

	select count(1) FROM   SY_RKGL_LDRKDJB  T1 WHERE 1=1

	
		AND T1.DJR = #{djr,jdbcType=VARCHAR}
	
	
		AND T1.DJSJ >= #{djsj,jdbcType=TIMESTAMP}

	
	SELECT
	
		
	
	FROM  SY_RKGL_LDRKDJB T1 WHERE 1=1
    
		AND T1.DJR = #{djr,jdbcType=VARCHAR}
	
	
		AND T1.DJSJ >= #{djsj,jdbcType=TIMESTAMP}
	
	
		ORDER BY T1.${sort} ${order}
